import React, {useEffect, useState,Component} from "react";

export default function Nav() {
    let [api, setApi]= useState([]);
  let[search, setSearch] = useState("")
  let change =  (e)=>{
    setSearch(e.target.value)
  }
  useEffect(()=>{
    let api= fetch(`https://pixabay.com/api/?key=46193687-1ee9f7465fd5eeca96a352d5f&q=${search}&image_type=photo`);
    api.then(res=>res.json()).then(val=>setApi(val.hits))
  })
    return (
      <>
        <div id="background_division">
        <div id="navigation">
          <div className="nav-items">
            <span className="pixabay-logo" id="one">Pixabay</span>
            <div className="search-container">
              <input 
                type="text" 
                placeholder="Search Pixabay" 
                className="search-input"
              />
              <select className="dropdown">
                <option>All Images</option>
                <option>Photos</option>
                <option>Videos</option>
                <option>Illustrations</option>
                <option>Vectors</option>
              </select>
            </div>
            <span className="explore">Explore</span>
            <button className="upload-btn" id="two">Upload</button>
          </div>
        </div>
        </div>
        <div id="middle_div">
        <h1 id="h1_one">Stunning royalty-free images & royalty-free stock</h1>
        <p id="para_one">Over 5 million+ high quality stock images, videos and music shared by our talented community.</p>
        <input type="text" placeholder="Search for all images  on  pixabay" className="input_1" onChange={change}/>
        <p id="para_2">Read more about the Content</p>
        <p id="para_3">Free images by yoskhu</p></div>
        <br />
        <div id="button_div">
        <button className="button_2">Home</button>
        <button className="button_2" >Photos</button>
        <button className="button_2">Illustartions</button>
        <button className="button_2">Vectors</button>
        <button className="button_2">Music</button>
        <button className="button_2">SoundEffects</button>
        <button className="button_2">Gifs</button>
        </div><br /><br />
        
        {api.filter(x=>{
          return (
            x.tags.toLowerCase().includes(search)
          )
        })
        .map(x=>{
          return(
            <img src={x.webformatURL} />
          )
        })}
      </>
    );
  
}
